import React from "react"

const Feed = ({coins}) => {

    //console.log('coins',coins)

    return (
        <tbody>
        {coins.map((coin, index) => (
            <tr key={index}>
                <td scope="row">
                    <div className="info">
                        <span className="date">{coin.cmc_rank}</span>
                    </div>
                </td>
                <td>
                    <div className="info">
                        <span className="date">{coin.name}</span>
                    </div>
                </td>
                <td>
                    <div className="info">
                        {new Intl.NumberFormat("en-US", {
                            style: "currency",
                            currency: "USD"
                        }).format(coin.quote.USD.price.toFixed(2))}
                    </div>
                </td>
                <td>
                    <div className="info">
                        <span className="date">{coin.quote.USD.percent_change_1h.toFixed(2)} %</span>
                    </div>
                </td>
                <td>
                    <div className="info">
                                <span className="date">
                                    {new Intl.NumberFormat("en-US", {
                                        style: "currency",
                                        currency: "USD"
                                    }).format(coin.quote.USD.market_cap)}

                                    </span>
                    </div>
                </td>
                <td>
                    <div className="info">
                                <span className="date">
                                    {new Intl.NumberFormat("en-US", {
                                        style: "currency",
                                        currency: "USD"
                                    }).format(coin.quote.USD.volume_24h)}
                            </span>
                    </div>
                </td>
            </tr>
        ))}
        </tbody>
    )
}

export default Feed
